function drawVisualization() {
    // Create and populate the data table.
    var data = new google.visualization.DataTable();
    data.addColumn('string', 'Date');
    data.addColumn('number', 'All IPs');
    data.addColumn('number', 'Unique IPs');
data.addRows(2);
data.setCell(0, 0, '2010');
data.setCell(0, 1, 179);
data.setCell(0, 2, 82);
data.setCell(1, 0, '2011');
data.setCell(1, 1, 267);
data.setCell(1, 2, 83);
// Create and draw the visualization.
new google.visualization.ColumnChart(document.getElementById('downloads_per_year')).
draw(data, {
width: 900, height: 400,
title: "Downloads per year" }
     );
}
google.setOnLoadCallback(drawVisualization);

