head.script:
function drawVisualization() {
    // Create and populate the data table.
    var data = new google.visualization.DataTable();
    data.addColumn('string', 'Country');
    data.addColumn('number', 'Download');
data.addRows(1);
data.setValue(0, 0, 'Germany');
data.setValue(0, 1, 6);
// Create and draw the visualization.
new google.visualization.PieChart(document.getElementById('chart_countries')).
draw(data, {width: 700, height: 500, title:"Downloads by Country"});
// Create and draw the visualization.
new google.visualization.ColumnChart(document.getElementById('bar_chart_countries')).
draw(data,
     {title:"Downloads by Country",
     legend:"none",
     width:800, height:400,
         vAxis: {title: "Downloads"}}
     );
}
google.setOnLoadCallback(drawVisualization);

function drawVisualization() {
    // Create and populate the data table.
    var data = new google.visualization.DataTable();
    data.addColumn('string', 'Month');
data.addColumn('number', '2010');
data.addColumn('number', '2011');
data.addRows(12);
data.setValue(0, 0 , 'Jan.');
data.setValue(1, 0 , 'Feb.');
data.setValue(2, 0 , 'Mar.');
data.setValue(3, 0 , 'Apr.');
data.setValue(4, 0 , 'Maj.');
data.setValue(5, 0 , 'Jun.');
data.setValue(6, 0 , 'Jul.');
data.setValue(7, 0 , 'Avg.');
data.setValue(8, 0 , 'Sep.');
data.setValue(9, 0 , 'Okt.');
data.setValue(10, 0 , 'Nov.');
data.setValue(11, 0 , 'Dec.');
data.setValue(8, 1, 2);
data.setValue(10, 1, 2);
data.setValue(11, 1, 1);
data.setValue(0, 2, 1);
// Create and draw the visualization.
new google.visualization.LineChart(document.getElementById('monthly_downloads')).
draw(data, {interpolateNulls: 'true',
width: 800, height: 400,
title: "Monthly Download Statistics" }
     );
}
google.setOnLoadCallback(drawVisualization);

function drawVisualization() {
    // Create and populate the data table.
    var data = new google.visualization.DataTable();
    data.addColumn('string', 'Version');
    data.addColumn('number', 'IP addresses');
    data.addColumn('number', 'Unique IP addresses');
data.addRows(4);
data.setValue(0, 0, 'vPoD-3.0');
data.setValue(0, 1, 1);
data.setValue(0, 2, 1);
data.setValue(1, 0, 'v3.1');
data.setValue(1, 1, 1);
data.setValue(1, 2, 1);
data.setValue(2, 0, 'v3.0');
data.setValue(2, 1, 2);
data.setValue(2, 2, 1);
data.setValue(3, 0, 'v2.1');
data.setValue(3, 1, 2);
data.setValue(3, 2, 1);
// Create and draw the visualization.
new google.visualization.ColumnChart(document.getElementById('totals_by_versions')).
draw(data, {reverseCategories: 'true',
width: 800, height: 400,
title: "Downloads by Versions" }
     );
}
google.setOnLoadCallback(drawVisualization);
